USE `twifi_dev` ;

-- =====================================================================================
-- 20140623 add column status to third_part_auth
ALTER TABLE `third_part_auth` 
ADD COLUMN `status` VARCHAR(20) NOT NULL DEFAULT 'NORMAL' COMMENT 'NORMAL | LOCKED | DELETED' AFTER `site_id`;
-- =====================================================================================

-- =====================================================================================
-- 20140623 add new table china_net_auth_request
DROP TABLE IF EXISTS `china_net_auth_request` ;

CREATE TABLE IF NOT EXISTS `china_net_auth_request` (
  `id` BIGINT NOT NULL AUTO_INCREMENT,
  `auth_log_id` BIGINT NULL,
  `wlanuserip` VARCHAR(60) NULL,
  `wlanacname` VARCHAR(400) NULL,
  `mscgip` VARCHAR(60) NULL,
  `userlocation` VARCHAR(600) NULL,
  `create_datetime` DATETIME NOT NULL DEFAULT now(),
  PRIMARY KEY (`id`))
ENGINE = InnoDB;
-- =====================================================================================

-- =====================================================================================
-- 20140623 update or insert virutal device component name
update `component` set `version` ='虚拟设备组件' where `version` = 'VIRTUAL COMPONENT';
-- =====================================================================================

