-- =====================================================================================
-- 20140625 upgrade component table to support firmware and component online upgrade
ALTER TABLE `component` 
CHANGE COLUMN `pkg_path` `pkg_path` VARCHAR(800) NULL DEFAULT NULL ,
ADD COLUMN `device_supported` TEXT NULL AFTER `description`,
ADD COLUMN `script_path` VARCHAR(800) NULL AFTER `pkg_path`,
ADD COLUMN `type` VARCHAR(45) NOT NULL DEFAULT 'FIRMWARE' COMMENT 'FIRMWARE | COMPONENT-PORTAL | COMPONENT-TASK' AFTER `version`;

ALTER TABLE `component` ADD COLUMN `status` VARCHAR(20) NOT NULL DEFAULT 'NORMAL' COMMENT 'NORMAL | LOCKED | DELETED | DRAFT' AFTER `type`;
ALTER TABLE `component` ADD COLUMN `ref_counter` BIGINT NOT NULL DEFAULT 0 AFTER `id`;
ALTER TABLE `component` ADD COLUMN `is_published` TINYINT(1) NOT NULL DEFAULT 0 AFTER `is_mandatory`;

update `component` set is_published = 1;
